import React, { useState, useEffect } from "react";
import CharacterList from "./CharacterList";
import Filter from "./Filter";
import getDataFromApi from "../services/api";

const App = () => {
    const [characters, setCharacters] = useState([]);
    const [name, setName] = useState("");
    const [planet, setPlanet] = useState("");

    useEffect(() => {
        getDataFromApi().then((data) => setCharacters(data));
    }, []);
    const handleFilter = (dataFilter) => {
        if (dataFilter === "name") {
            setName(dataFilter.value);
        } else if (dataFilter === "planet") {
            setPlanet(dataFilter.value);
        }
    };
    const filterCharacters = characters.filter((character) => {
        return character.name.toLowerCase().includes(name.toLowerCase());
    });
    return ( <
        >
        <
        section >
        <
        h1 > Rick y Morty < /h1> <Filter handleFilter={handleFilter} / > { " " } <
        /section>{" "} <
        CharacterList characters = { characters } /* {filterCharacters} */
        />{" "} <
        />
    );
};

export default App;
